<h3>新規アカウント登録</h3><form id="formCreate" class="form-horizontal" role="form" action="<?php echo Uri::create('/auth/created');?>" method="POST">	<!-- CSRF対策 -->	<input type="hidden" name="<?php echo \Config::get('security.csrf_token_key');?>" value="<?php echo \Security::fetch_token();?>" />	<div class="form-group">		<label class="col-md-3 control-label">ユーザー名:</label>		<div class="col-md-5">			<input class="form-control" type="text" name="username" value="">		</div>	</div>	<div class="form-group">		<label class="col-md-3 control-label">Ｅメール:</label>		<div class="col-md-5">			<input class="form-control" type="email" name="email" value="">		</div>	</div>	<div class="form-group">		<label class="col-md-3 control-label">パスワード:</label>		<div class="col-md-5">			<input class="form-control" type="password" name="password" value="">		</div>	</div>	<div class="form-group">		<label class="col-md-3 control-label">パスワード確認:</label>		<div class="col-md-5">			<input class="form-control" type="password" name="password2" value="">		</div>	</div>	<div class="form-group">		<label class="col-md-3 control-label"></label>		<div class="col-md-5">			<input type="submit" class="btn btn-primary" value="ユーザー作成">			<span>&nbsp;</span>			<input type="reset" class="btn btn-default" value="リセット">		</div>	</div></form><!-- モーダルダイアログ --><div class="modal" id="alertModal" tabindex="-1" role="dialog" aria-labelledby="staticModalLabel" aria-hidden="true" data-show="true" data-keyboard="false" data-backdrop="static">	<div class="modal-dialog">		<div class="modal-content">			<div class="modal-header">				<button type="button" class="close" data-dismiss="modal">					<span aria-hidden="true">&#215;</span><span class="sr-only">閉じる</span>				</button>				<h4 class="modal-title">入力エラー</h4>			</div>			<div class="modal-body">				<p class="recipient">エラー内容</p>			</div>			<div class="modal-footer">				<button type="button" class="btn btn-default" data-dismiss="modal">閉じる</button>			</div>		</div>	</div></div><script type="text/javascript">$(function() {	// パスワード一致チェック	$('#formCreate').on('submit', function(){		$modal = $('#alertModal');		if($('input[name=password]').val() != $('input[name=password2]').val()) {			//alert('パスワードが一致しません');			$modal.find('.modal-body .recipient').text('パスワードが一致しません。');			$modal.modal();			return false;		}		return true;	});});</script>