<h3>アカウント削除</h3><form class="form-horizontal" role="form" action="<?php echo Uri::create('/auth/removed');?>" method="POST">	<!-- CSRF対策 -->	<input type="hidden" name="<?php echo \Config::get('security.csrf_token_key');?>" value="<?php echo \Security::fetch_token();?>" />	<div class="form-group">		<label class="col-md-3 control-label">ユーザー名:</label>		<div class="col-md-5">			<?php echo $username ?>			<input type="hidden" name="username" value="<?php echo $username ?>">		</div>	</div>	<div class="form-group">		<label class="col-md-3 control-label"></label>		<div class="col-md-5">			<p>削除しますか？</p>			<input type="submit" value="削除" class="btn" />		</div>	</div></form><div class="text-center">	<p><a href="<?php echo Uri::base(); ?>">トップへ</a></p></div>